svg.script:

    window.onload = function() {
			let slideAnimationTime = 0;
			const scene1Delay = 1500;
			const scene2Delay = scene1Delay + slideAnimationTime + 1700;
			const scene3Delay = scene2Delay + 3000;
			const scene4Delay = scene3Delay + 3000;
			const scene5Delay = scene4Delay + 4500;
			const scene6Delay = scene5Delay + 3300;
			const scene7Delay = scene6Delay + 1500;
			const scene8Delay = scene7Delay + 3500;

			let scene1timer;
			let scene2timer;
			let scene3timer;
			let scene4timer;
			let scene5timer;
			let scene6timer;
			let scene7timer;
			let scene8timer;

			const getRandomDeg = () => Math.floor(Math.random() * (90 - -90 + 1)) + -90;

			function addAnimationDelay(elems, initDelay = 0) {
				const delayAmount = 100; 
				elems.forEach((elem, index) => {
					const time = delayAmount * index + initDelay;
					slideAnimationTime = time + 400 > slideAnimationTime ? time + 400 : slideAnimationTime;
					elem.style.animationDelay = `${time}ms`;
				})
			}

			const animatedDocsPageWrap = document.getElementById("animatedDocsPageWrap");

			// scene 1
			const slideLines = document.querySelectorAll(".slideLine");
      const leftSlideLines = document.querySelectorAll("#slideTextGroupLeftDoc .slideLine");
			const rightSlideLines = document.querySelectorAll("#slideTextGroupRightDoc .slideLine");
			const bigDocTopSlideLines = document.querySelectorAll("#slideTextGroupBigDoc .slideLine.top");
			const bigDocBottomSlideLines = document.querySelectorAll("#slideTextGroupBigDoc .slideLine.bottom");
			addAnimationDelay(leftSlideLines);
			addAnimationDelay(rightSlideLines);
			addAnimationDelay(bigDocTopSlideLines, 150);
			addAnimationDelay(bigDocBottomSlideLines, 150);

			// scene 2
			const bigDocSlideLines = document.querySelectorAll(".rotateWrap");
			const leftSmallDoc = document.getElementById("left_small_doc");
			const rightSmallDoc = document.getElementById("right_small_doc");
			const fallWrap = document.getElementById("fallWrap");
			bigDocSlideLines.forEach(slideLine => {
				const transformOriginY = slideLine.querySelector(".slideLine").getAttribute("y");
				slideLine.style.transformOrigin = `188px ${transformOriginY}px`;
			});

			// scene 3
			const blinkingCursor = document.getElementById("splashBlinkingCursor");

			// scene 4
			// blinkingCursor
			const longWriteLines = document.querySelectorAll("#writeTextGroup .writeLine:not(.short)");
			const writeLines = document.querySelectorAll("#writeTextGroup .writeLine");
			longWriteLines.forEach((writeLine, index) => {
				const delay = `${index * 400 + 600}ms`;
				const writeCursor = writeLine.querySelector(".writeCursor");
				writeLine.style.animationDelay = delay;
				writeCursor.style.animationDelay = delay;
			});

			// scene 5
			const whiteOuts = [
				{elem: document.getElementById("whiteOutGroup1"), x: 30},
				{elem: document.getElementById("whiteOutGroup2"), x: -30},
				{elem: document.getElementById("whiteOutGroup3"), x: 44},
				{elem: document.getElementById("whiteOutGroup4"), x: -40},
				{elem: document.getElementById("whiteOutGroup5"), x: -30},
				{elem: document.getElementById("whiteOutGroup6"), x: 40},
				{elem: document.getElementById("whiteOutGroup7"), x: -30}
			];
			function getDur(amount) {
				return Math.abs(amount) * 10;
			}
			let whiteOutDelay = 0;
			whiteOuts.forEach(whiteOut => {
				const dur = getDur(whiteOut.x);
				const cursor = whiteOut.elem.querySelector(".whiteOutCursor");
				whiteOut.elem.style.animationDelay = `${whiteOutDelay}ms`;
				cursor.style.animationDelay = `${whiteOutDelay}ms`;
				whiteOut.elem.style.animationDuration = `${dur}ms`;
				cursor.style.animationDuration = `${dur}ms`;

				whiteOutDelay = whiteOutDelay + dur;
			})

			// scene 6
			const lastTextGroup = document.getElementById("lastTextGroup");

			// scene 7
			const cloudGroup = document.getElementById("cloudGroup");

			// scene 8
			// cloudGroup
			// blinkingCursor

			function play() {
				scene8timer = window.setTimeout(() => {
					cloudGroup.classList.remove("visible");
					blinkingCursor.classList.add("blink");
				}, scene8Delay);

				scene7timer = window.setTimeout(() => {
					cloudGroup.classList.add("visible");
				}, scene7Delay);

				scene6timer = window.setTimeout(() => {
					lastTextGroup.classList.add("hide");
				}, scene6Delay);

				scene5timer = window.setTimeout(() => {
					whiteOuts.forEach(whiteOut => whiteOut.elem.classList.add("whiteOutAnimate"));
				}, scene5Delay);

				scene4timer = window.setTimeout(() => {
					blinkingCursor.classList.remove("blink");
					writeLines.forEach(writeLine => writeLine.classList.add("animate"));
				}, scene4Delay);

				scene3timer = window.setTimeout(() => {
					blinkingCursor.classList.add("blink");
				}, scene3Delay);

				scene2timer = window.setTimeout(() => {
					bigDocSlideLines.forEach(slideLine => slideLine.style.transform = `rotate(${getRandomDeg()}deg)`);
					fallWrap.classList.add("scatter");
					leftSmallDoc.classList.add("hide");
					rightSmallDoc.classList.add("hide");
				}, scene2Delay);

				scene1timer = window.setTimeout(() => {
					slideLines.forEach(slideLine => slideLine.classList.add("animate"));
				}, scene1Delay);
			}
			play();

			function jumpToEnd() {
				// end state
				leftSmallDoc.classList.add("removed");
				rightSmallDoc.classList.add("removed");
				blinkingCursor.classList.add("blink");

				// scene 1 reset
				slideLines.forEach(slideLine => slideLine.classList.add("removed"));

				// scene 2 reset
				bigDocSlideLines.forEach(slideLine => slideLine.classList.add("removed"));
				fallWrap.classList.add("removed");

				// scene 4 reset
				writeLines.forEach(writeLine => writeLine.classList.add("removed"));

				// scene 5 reset
				whiteOuts.forEach(whiteOut => whiteOut.elem.classList.add("removed"));

				// scene 6 reset
				lastTextGroup.classList.add("removed");

				// scene 7 reset
				cloudGroup.classList.add("removed");
			}

			document.addEventListener("visibilitychange", () => {
				if (document.visibilityState !== "visible") {
					const timers = [scene1timer, scene2timer, scene3timer, scene4timer, scene5timer, scene6timer, scene7timer, scene8timer];
					timers.forEach(timer => window.clearTimeout(timer));
					jumpToEnd();
				}
			});

    };
